sys_script_client.script:
function onLoad() {

	var parecer1 = g_form.getValue('parecer1').length;
	var parecer2 = g_form.getValue('parecer2').length;
	//var parecer3 = g_form.getValue('parecer3').length;

	var situacao = g_form.getValue('situacao');
	var currentUser = g_user.userID;

   //Se situação da Súmula = Em Cadastramento
   if (situacao == 1) {
	  //Inibe os três pareceres de votação caso a súmula esteja Em Construção.
      g_form.setDisplay('parecer1', false);
      g_form.setDisplay('parecer2', false);
      g_form.setDisplay('parecer3', false);      
	  g_form.setDisplay('responsavel1', false);      
	  g_form.setDisplay('responsavel2', false);      
	  g_form.setDisplay('responsavel3', false);      
      g_form.setDisplay('u_cbparecer1', false);
      g_form.setDisplay('u_cbparecer2', false);
      g_form.setDisplay('u_cbparecer3', false);
	   //Popula campo Responsável BB Consórcio com o usuário logado
	   g_form.setValue('responsavel_bb_consorcio', currentUser);
   } else if (situacao == 2) {
	 //Nao houve Primeito Parecer
     if (parecer1 == 0) {
		 //Popula usuario logado e seta campos obrigatórios.
		 g_form.setValue('responsavel1', currentUser);
		 g_form.setMandatory('parecer1', true);
		 g_form.setMandatory('u_cbparecer1', true);
		 //Inibe outros pareceres nao utilizados.
		 g_form.setDisplay('parecer2', false);
         g_form.setDisplay('parecer3', false);      
	     g_form.setDisplay('responsavel2', false);      
	     g_form.setDisplay('responsavel3', false);      
         g_form.setDisplay('u_cbparecer2', false);
         g_form.setDisplay('u_cbparecer3', false);

	 } else if (parecer2 == 0) { //Houve primeiro voto mas nao houve o segundo
		 //Protege Primeiro Voto
		 g_form.setReadOnly('parecer1',true);
		 g_form.setReadOnly('u_cbparecer1',true);
		 //Popula usuario logado e seta campos obrigatórios.
		 g_form.setValue('responsavel2', currentUser);
		 g_form.setMandatory('parecer2', true);
		 g_form.setMandatory('u_cbparecer2', true);
		 //Inibe outros pareceres nao utilizados.
         g_form.setDisplay('parecer3', false);      
	     g_form.setDisplay('responsavel3', false);      
         g_form.setDisplay('u_cbparecer3', false);

	 }
   }

}
